package downloader.jobs;

import org.apache.commons.codec.binary.Base64;

import java.io.*;
import java.net.URL;

public class DownloadImage {

    public static void saveImage(String folderPath, String imageUrl) throws IOException {
        InputStream is;
        String destName;

        URL url = new URL(imageUrl);
        String fileName = url.getFile();
        destName = folderPath + "/" + fileName.substring(fileName.lastIndexOf("/")).split("\\?")[0];
        is = url.openStream();
//        try {
//            URL url = new URL(imageUrl);
//            String fileName = url.getFile();
//            destName = folderPath + "/" + fileName.substring(fileName.lastIndexOf("/")).split("\\?")[0];
//            is = url.openStream();
//        } catch (Exception ex)
//        {
//            destName = folderPath + "/" + "someRandom";
//            is = new FileInputStream(imageUrl);
//        }

        OutputStream os = new FileOutputStream(destName);

        byte[] b = new byte[2048];
        int length;

        while ((length = is.read(b)) != -1) {
            os.write(b, 0, length);
        }

        is.close();
        os.close();
    }


    public static void main(String[] args) {
        try
        {
//            saveImage("/Users/luis/Desktop/", );
//            System.out.println(base64UrlDecode("data:image/jpeg;base64,"));
            saveImage("/Users/luis/Desktop", "data:image/jpeg;base64,");
        } catch (Exception exx)
        {
            exx.printStackTrace();
        }
    }

    public static String base64UrlDecode(String input) {
        String result = null;
        Base64 decoder = new Base64(true);
        byte[] decodedBytes = decoder.decode(input);
        result = new String(decodedBytes);
        return result;
    }

}
